Queries.Template:
{
  "query": {
    "bool" : {
      "must" : [
        { "term" : { "Content.ContentItem.ContentType" : "BlogPost" }},
        { "term" : { "Content.ContentItem.Published" : "true" }},
        { "term" : { "Content.ContentItem.Latest" : "true" }},
        {% if terms != blank and terms != "*" %}
        {
          "bool": {
            "should": [
              { "wildcard" : { "Content.ContentItem.FullText" : "*{{ terms | downcase }}*", "boost": 1 }}
            ]
          }
        }
        {% endif %}
      ]
    }
  },
  "sort": [
    { "Content.ContentItem.ModifiedUtc": { "order": "desc" } },
    { "Content.ContentItem.CreatedUtc": { "order": "desc" } }
  ],
  "size": {{ size | default: 6 }},
  "from": {{ from | default: 0 }}
}

{
  "query": {
    "bool" : {
      "must" : [
        { "term" : { "Content.ContentItem.ContentType" : "Project" }},
        { "match": { "Content.ContentItem.FullText": { 
          	"query": "{{ terms | default: "project" }}",
          	"fuzziness": "AUTO",
			"operator":  "or" 
        }}}
      ],
      "must_not" : {
        "term" : { "Content.ContentItem.DisplayText.Normalized" : "{{ terms | default: "project" | downcase }}" }
      }
    }
  },
  "size": {{ size | default: 4 }},
  "from": {{ from | default: 0 }}
}

LiquidPart.Liquid:
{% assign categories = Content.Alias["categories"] %}
<div class="card my-4">
    <h5 class="card-header">Categories</h5>
    <div class="card-body">
        <div class="row">
            {% assign mod = categories.Content.TaxonomyPart.Terms.size / 2 %}
            {% for term in categories.Content.TaxonomyPart.Terms %}
            {% assign count = forloop.index0 | modulo:mod %}
                {% if count == 0 %}
                <div class="col-lg-6">
                    <ul class="list-unstyled mb-0">
                {% endif %}
                        <li>
                            {{ term | shape_build_display: "Summary" | shape_render }}
                        </li>
                {% if count == 2 %}
                    </ul>
                </div>
                {% endif %}
            {% endfor %}
        </div>
    </div>
</div>

{% assign currentItem = Content["slug:" + Request.Path]  %}

{% if currentItem.ContentType == "Taxonomy" %}
    {% assign termContentId = Request.Path | split: "/" | last | split: "-" | first %}
    {% assign parentCategories = termContentId | inherited_terms: currentItem.ContentItemId %}
    {% assign termContentItem = parentCategories | last %}
{% endif %}

{% if currentItem %}
<!-- Page Heading -->
<h1 class="mt-4 mb-3">{{ currentItem.Content.TitlePart.Title }}
    <small>{{ currentItem.Content.SubtitlePart.Subtitle.Text | default: termContentItem.DisplayText }}</small>
</h1>

{% assign path = currentItem.Content.AutoroutePart.Path %}
{% if termContentItem %}
	{% assign path = path + "/" + termContentItem.DisplayText %}
{% endif %}
{% assign breadcrumb = path | split: "/" %}
<ol class="breadcrumb">
    <li class="breadcrumb-item">
        <a href="{{ "~/" | href }}">Home</a>
    </li>
    {% for path in breadcrumb %}
  		{% if forloop.last %}
    		<li class="breadcrumb-item active">{{ path | capitalize }}</li>
		{% else %}
  			{% assign url = "~/" + path %}
  			<li class="breadcrumb-item"><a href="{{ url | href }}">{{ path | capitalize }}</a></li>
    	{% endif %}
    {% endfor %}
</ol>
{% endif %}

{% assign project = Content.Slug[Request.Path] %}
{% if project %}
{% assign results = Queries.SearchRelatedProjects | query: terms: project.Content.TitlePart.Title %}
<h3 class="my-4">Related Projects</h3>
<div class="row">
    {% for item in results %}
    <div class="col-md-3 col-sm-6 mb-4">
        {% a display_for:item %}
        <img class="img-fluid" src="{{ item.Content.Project.Images.Paths[0] | asset_url | resize_url: width:500, height:300, mode:"crop" }}" alt="{{ item.Content.TitlePart.Title }}">
        {% enda %}
    </div>
    {% endfor %}
</div>
{% endif %}

{% assign pageSize = Request.Query.size[0] | abs %}
{% if pageSize <= 0 %}{% assign pageSize = Site.PageSize %}{% endif %}

{% assign queryPageSize = pageSize + 1 %}
{% assign queryFrom = Request.Query.before[0] | default: Request.Query.after[0] | abs %}

{% comment %}execute query{% endcomment %}
{% assign results = Queries.SearchBlogPosts | query: size: queryPageSize, from: queryFrom, terms: Request.Query["Terms"] %}
{% assign hasMore = results.size > pageSize %}

{% comment %}pagination{% endcomment %}
{% assign beforeQueryFrom = -1 %}
{% assign previousClass = "disabled" %}
{% if queryFrom > 0 %}
	{% assign beforeQueryFrom = queryFrom - pageSize %}
    {% if beforeQueryFrom < 0 %}{% assign beforeQueryFrom = 0 %}{% endif %}
	{% assign previousClass = nil %}
{% endif %}

{% assign afterQueryFrom = -1 %}
{% assign nextClass = "disabled" %}
{% if hasMore %}
	{% assign afterQueryFrom = queryFrom + pageSize %}
	{% assign nextClass = nil %}
{% endif %}

{% assign beforeString = beforeQueryFrom | downcase %}
{% assign afterString = afterQueryFrom | downcase %}

{% assign previousText = "← Newer" | t %}
{% assign nextText = "Older →" | t %}

{% comment %}build pager shape{% endcomment %}
{% assign pagerShape = "PagerSlim" | shape_new | shape_properties: before: beforeString, after: afterString, content_item: Model.ContentItem, page_size: pageSize %}
{% shape_pager pagerShape classes: "pagination justify-content-center", item_classes: "page-item", 
    previous_text: previousText, next_text: nextText,
    previous_class: previousClass, next_class: nextClass %}

<div class="row">
	<div class="col-md-8">
	{% for item in results %}
      	{% if forloop.index <= pageSize %}
			{{ item | shape_build_display: "Summary" | shape_properties: show_widgets: true | shape_render }}
      	{% endif %}
    {% endfor %}

    {{ pagerShape | shape_render }}
	</div>

    <div class="col-md-4">
        {% render_section "ContentAside", required: false %}
    </div>
</div>

